import { createApp } from "vue";
import { createRouter, createWebHistory } from "vue-router";
import App from "./App.vue";
import LightBootstrap from "./light-bootstrap-main";
import routes from "./routes/routes";
import "./registerServiceWorker";

// Importa tu SidebarPlugin desde el archivo index.js
import SidebarPlugin from "./components/SidebarPlugin";

// Usar VueRouter
const router = createRouter({
  history: createWebHistory(),
  routes,
  linkActiveClass: "nav-item active",
  scrollBehavior: (to) => {
    if (to.hash) {
      return { selector: to.hash };
    } else {
      return { x: 0, y: 0 };
    }
  },
});

// Crea la instancia de la aplicaci贸n
const app = createApp(App);

// Usa VueRouter
app.use(router);

// Usa LightBootstrap
app.use(LightBootstrap);

// Usa el SidebarPlugin
app.use(SidebarPlugin);

// Monta la aplicaci贸n en el elemento con el ID "app"
app.mount("#app");
